mapper.select:
select *,
ifnull((select user_id from user where user_no=comment.user_no),'관리자') as username
from comment where board_no=#{board_no}

mapper.insert:
INSERT INTO board_comment (
	board_no,board_comment, regdate, user_no
) VALUES (			
	#{board_no},
	#{board_comment},
	NOW(),
	#{user_no}			
)
